)

	INSERT INTO approval_doc(
		  approval_doc_no
		, approval_type
		, emp_no
		, approval_state
		, doc_title
		, first_approval
		, final_approval
		, doc_first_content
		, doc_second_content
		, doc_third_content
		, start_date
		, end_date
		, reg_id
		, reg_time
		, mod_id
		, mod_time
		, use_yn
	)VALUES(
	  	  CURRENT_TIMESTAMP(5)
		, #{approvalType}
		, #{empNo}
		, #{approvalState}
		, #{docTitle}
		, #{firstApproval}
		, #{finalApproval}
		, #{docFirstContent}
		, #{docSecondContent}
		, #{docThirdContent}
		, #{startDate}
		, #{endDate}
		, #{regId}
		, NOW()
		, #{modId}
		, NOW()
		, #{useYn}
	)


        SELECT CAST(MAX(approval_doc_no) AS CHAR) 
        FROM approval_doc 
        WHERE reg_id=#{regId}
        ORDER BY reg_time DESC LIMIT 1

INSERT INTO file_upload(
	file_no, reference_no, original_file_name
