mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY e.emp_hiredate) AS NO, e.emp_no empNo, e.emp_name empName, dept_code.cd_nm empDept, grade_code.cd_nm empGrade, lv.year_count yearCnt, e.use_yn useYn FROM employee AS e LEFT JOIN `leave` AS lv ON e.emp_no = lv.emp_no LEFT JOIN common_code AS dept_code ON dept_code.cd = e.emp_dept AND dept_code.crp_cd = 'E001' LEFT JOIN common_code AS grade_code ON grade_code.cd = e.emp_grade AND grade_code.crp_cd = 'E002' WHERE 1=1
AND e.emp_hiredate >= #{startDate}
AND e.emp_hiredate <= #{endDate}
AND e.use_yn = #{useYn}
AND e.emp_dept = #{empDept}
AND e.emp_grade = #{empGrade}
AND e.emp_no = #{empNo}
LIMIT #{currentPage}, #{rowPerPage};

		 SELECT 
		 	COUNT(*)
   		FROM employee e
    	INNER JOIN 
    		`leave` lv 
   		ON 
   			e.emp_no = lv.emp_no
    	WHERE
	        1=1

	            AND e.emp_hiredate >= #{startDate}


	            AND e.emp_hiredate <= #{endDate}


	            AND e.use_yn = #{useYn}


	            AND e.emp_dept = #{empDept}


	            AND e.emp_grade = #{empGrade}


	            AND e.emp_no = #{empNo}

SELECT e.emp_name empName, emp_dept.cd_nm empDept, DATE_FORMAT(lh.start_time, '%Y-%m-%d') startTime, DATE_FORMAT(lh.end_time, '%Y-%m-%d') endTime, lh.leave_count leaveCount, leave_content.cd_nm leaveContent FROM leave_history AS lh LEFT JOIN employee AS e ON lh.emp_no = e.emp_no LEFT JOIN common_code AS leave_content ON leave_content.cd = lh.leave_type AND leave_content.crp_cd = 'E003' LEFT JOIN common_code AS emp_dept ON emp_dept.cd = e.emp_dept AND emp_dept.crp_cd = 'E001' WHERE e.emp_no = #{empNo} AND lh.use_yn = 'Y'
AND DATE(lh.start_time) >= #{startDate}
AND DATE(lh.end_time) <= #{endDate}
ORDER BY lh.leave_history_no DESC LIMIT #{currentPage}, #{rowPerPage}

SELECT COUNT(*) FROM leave_history AS lh WHERE lh.emp_no = #{empNo} AND lh.use_yn = 'Y'
AND DATE(lh.start_time) >= #{startDate}
AND DATE(lh.end_time) <= #{endDate}

SELECT ROW_NUMBER() OVER (ORDER BY ch.reg_time DESC) AS NO, DATE(ch.start_time) date, em.emp_name empName, ch.emp_no empNo, DATE_FORMAT(ch.start_time, '%H:%i:%s') strTime, DATE_FORMAT(ch.end_time, '%H:%i:%s') endTime FROM commute_history AS ch LEFT JOIN employee em ON ch.emp_no = em.emp_no WHERE 1=1
AND ch.start_time >= #{startDateHidden}
AND ch.end_time <= #{endDateHidden}
AND ch.emp_no = #{empNo}
AND em.emp_Name = #{empName}
LIMIT #{currentPage}, #{rowPerPage}

SELECT COUNT(*) FROM commute_history AS ch LEFT JOIN employee em ON ch.emp_no = em.emp_no WHERE 1=1
AND ch.start_time >= #{startDateHidden }
AND ch.end_time <= #{endDateHidden}
AND em.emp_no = #{empNo}
AND em.emp_name = #{empName}

SELECT e.emp_no empNo, e.emp_name empName, dept.cd_nm empDept, grade.cd_nm empGrade, l.year_count curYearLeave, l.month_count curMonthLeave, l.year_count - COALESCE(SUM(CASE WHEN lh.leave_type IN ('Y', 'H') THEN lh.leave_count ELSE 0 END), 0) AS yearCnt, l.month_count - COALESCE(SUM(CASE WHEN lh.leave_type = 'M' THEN lh.leave_count ELSE 0 END), 0) AS monthCnt, e.use_yn useYN FROM `leave` l LEFT JOIN leave_history lh ON l.emp_no = lh.emp_no AND lh.start_time >= DATE_FORMAT(NOW() ,'%Y-01-01 00:00:00') AND lh.end_time <= DATE_FORMAT(NOW() ,'%Y-12-31 23:59:59') LEFT JOIN employee AS e ON l.emp_no = e.emp_no LEFT JOIN common_code dept ON dept.cd = e.emp_dept AND dept.crp_cd = 'E001' LEFT JOIN common_code grade ON grade.cd = e.emp_grade AND grade.crp_cd = 'E002' WHERE l.use_yn = 'Y'
AND e.emp_no = #{empNo}
AND e.emp_name = #{empName}
GROUP BY e.emp_no, e.emp_name, dept.cd_nm, grade.cd_nm, l.year_count, l.month_count, e.use_yn ORDER BY e.emp_hiredate DESC LIMIT #{currentPage}, #{rowPerPage}

SELECT COUNT(*) FROM `leave` AS l LEFT JOIN employee AS e ON l.emp_no = e.emp_no LEFT JOIN common_code dept ON dept.cd = e.emp_dept AND dept.crp_cd = 'E001' LEFT JOIN common_code grade ON grade.cd = e.emp_grade AND grade.crp_cd = 'E002' WHERE 1=1
AND e.emp_no = #{empNo}
AND e.emp_name = #{empName}

mapper.update:
UPDATE note_history SET mod_time = NOW(), receiver_delete = CASE WHEN receiver_id = #{empNo} THEN 'N' ELSE receiver_delete END, sender_delete = CASE WHEN sender_id = #{empNo} THEN 'N' ELSE sender_delete END WHERE note_id IN
#{noteId}
AND ( (receiver_id = #{empNo} AND receiver_delete = 'Y') OR (sender_id = #{empNo} AND sender_delete = 'Y') )
