mapper.select:
SELECT g.gst_id gstId, g.gst_name gstName, r.room_dong roomDong, r.room_ho roomHo FROM guest g INNER JOIN room r ON g.room_no = r.room_no WHERE g.use_yn = 'Y' AND g.gst_type = 'R'
AND (g.gst_name LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{beginRow}, #{rowPerPage}

SELECT g.gst_id gstId, g.gst_name gstName, r.room_dong dong, r.room_type type, r.room_ho ho FROM guest g INNER JOIN room r ON g.room_no = r.room_no WHERE g.room_no IS NOT NULL AND g.use_yn = 'Y' AND g.gst_type = 'R'
AND r.room_dong = #{dong}
AND r.room_type = #{type}
AND r.room_ho = #{ho}
AND (g.gst_name LIKE CONCAT('%', #{searchWord}, '%') OR #{searchWord} IS NULL)
LIMIT #{beginRow}, #{rowPerPage}

SELECT count(*) FROM guest g INNER JOIN room r ON g.room_no = r.room_no WHERE g.room_no IS NOT NULL AND g.use_yn = 'Y' AND g.gst_type = 'R'
AND r.room_dong = #{dong}
AND r.room_type = #{type}
AND r.room_ho = #{ho}
AND (g.gst_name LIKE CONCAT('%', #{searchWord}, '%') OR #{searchWord} IS NULL)

mapper.insert:
INSERT INTO file_upload(
	file_no, reference_no, original_file_name
	, file_name, file_size, file_type
	, reg_time, mod_time, reg_id, mod_id, use_yn
)VALUES(
	CURRENT_TIMESTAMP(5), #{referenceNo}, #{OriginalFileName}
	, #{fileName}, #{fileSize}, #{fileType}
	, NOW(), NOW(), #{regId}, #{modId}, #{useYN}
)

mapper.update:
UPDATE 
	file_upload
SET 
	original_file_name = #{OriginalFileName}
	, file_name = #{fileName}
	, file_size = #{fileSize}
	, file_type = #{fileType}
	, mod_id = #{modId}
	, mod_time = NOW()

WHERE 
	reference_no = #{referenceNo}
