mapper.select:
SELECT COUNT(*) FROM news_notice AS nn LEFT JOIN common_code AS grade_comm ON nn.grade = grade_comm.cd AND grade_comm.crp_cd = 'W004' LEFT JOIN common_code AS category_comm ON nn.category = category_comm.cd AND category_comm.crp_cd = 'W005' LEFT JOIN employee AS e ON e.emp_no = nn.reg_id WHERE nn.use_yn = 'Y' AND nn.grade IN (1,2,3) AND nn.category IN (1,2,3)
AND category_comm.cd_nm = #{noticeType}
AND (nn.title LIKE CONCAT('%', #{searchWord}, '%') OR nn.content LIKE CONCAT('%', #{searchWord}, '%'))

SELECT ROW_NUMBER() OVER(ORDER BY nn.news_no DESC) no, nn.news_no newsNo, nn.title title, e.emp_name empName, nn.content content, grade_comm.cd_nm grade, category_comm.cd_nm category, DATE_FORMAT(nn.reg_time, '%Y-%m-%d') regTime FROM news_notice AS nn LEFT JOIN common_code AS grade_comm ON nn.grade = grade_comm.cd AND grade_comm.crp_cd = 'W004' LEFT JOIN common_code AS category_comm ON nn.category = category_comm.cd AND category_comm.crp_cd = 'W005' LEFT JOIN employee AS e ON e.emp_no = nn.reg_id WHERE nn.use_yn = 'Y'
AND category_comm.cd_nm = #{noticeType}
AND (nn.title LIKE CONCAT('%', #{searchWord}, '%') OR nn.content LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{currentPage}, #{rowPerPage}
