mapper.insert:
INSERT INTO schedules(
	schedule_no
	, title
	, type
	, content
	, start_date
	, end_date
	, reg_id
	, reg_time
	, mod_id
	, mod_time
	, use_yn
)VALUES(
	CURRENT_TIMESTAMP(5)
	, #{title}
	, #{type}
	, #{content}
	, #{startDate}
	, #{endDate}
	, #{regId}
	, NOW()
	, #{modId}
	, NOW()
	, 'Y'
);

mapper.update:
UPDATE 
	schedules
SET
	title = #{title}
	, type = #{type}
	, content = #{content}
	, start_date = #{startDate}
	, end_date = #{endDate}
	, mod_id = #{modId}
	, mod_time = NOW()
WHERE 
	schedule_no = #{scheduleNo}
